import { createSlice, Dispatch } from '@reduxjs/toolkit'
import weatherService from '../services/weatherService'
import { weatherReport } from '../@types/weatherReport'

const initialState: weatherReport = {
    name: null,
    weather: null,
    main: null,
    wind: null
}

const weatherReportSlice = createSlice({
    name: 'weatherReport',
    initialState,
    reducers: {
        setWeather: (state, action) => {
            const weatherReport = action.payload
            state = weatherReport
            return state
        }
    }
})

export const fetchWeatherReport = (city: string) => {
    return async (dispatch: Dispatch) => {
        const weatherReport = await weatherService.getCity(city)
        dispatch(setWeather(weatherReport))
    }
}

export const { setWeather } = weatherReportSlice.actions

const weatherReportReducer = weatherReportSlice.reducer

export default weatherReportReducer
