import { createSlice, Dispatch } from '@reduxjs/toolkit'

const initialState = true

const hideWeatherSlice = createSlice({
    name: 'hideWeather',
    initialState,
    reducers: {
        setWeatherStatus: (state, action) => {
            state = action.payload
        }
    }
})

export const showWeather = () => {
    return (dispatch: Dispatch) => {
        dispatch(setWeatherStatus(false))
    }
}

export const {setWeatherStatus} = hideWeatherSlice.actions

const hideWeatherReducer = hideWeatherSlice.reducer

export default hideWeatherReducer
