import React, { useState } from 'react'
import { useAppDispatch, useTypedSelector } from '../store'
import { fetchWeatherReport } from '../reducers/weatherReportReducer'

const CitySearchBox = () => {
    const dispatch = useAppDispatch()
    const [search, setSearch] = useState('')
    const weatherReport = useTypedSelector(state => state.WeatherReport.weatherReport)
    const isLoading = useTypedSelector(state => state.WeatherReport.isLoading)
    //const error = useTypedSelector(state => state.WeatherReport.error)

    const handleSearchChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        setSearch(event.target.value)
    }

    const getWeather = async () => {
        await dispatch(fetchWeatherReport(search))
        while (isLoading === true) {
            console.log(weatherReport?.name)
        }
        setSearch('')
    }

    return (
        <div className="Find-city">
            City: <input name="search" value={search}
                onChange={handleSearchChange}
            /> <button onClick={() => getWeather()}>show weather</button>
        </div>
    )
}

export default CitySearchBox
